mapper.select:
SELECT b.bbs_idx,b.bbs_subject,b.bbs_content,b.reg_date,b.views,b.reco_count,b.nickname,b.pet_idx,b.category_name,p.kind,p.bday,p.pet_name,p.kg
,(select pi.newfilename from pet_img pi JOIN pet p ON p.pet_idx = pi.pet_idx WHERE p.pet_idx=b.pet_idx) pet_newfilename
FROM bbs b JOIN pet p ON p.pet_idx(+) = b.pet_idx WHERE b.bbs_idx=#{param1}

SELECT b.bbs_idx,b.bbs_subject,b.bbs_content,b.reg_date,b.views,b.reco_count,b.nickname,b.pet_idx,b.category_name,p.kind,p.bday,p.pet_name,p.kg
,(select pi.newfilename from pet_img pi JOIN pet p ON p.pet_idx = pi.pet_idx WHERE p.pet_idx=b.pet_idx) pet_newfilename
FROM bbs b JOIN pet p ON p.pet_idx(+) = b.pet_idx WHERE b.bbs_idx=#{bbs_idx}

SELECT b.bbs_idx, b.bbs_subject, b.bbs_content, b.reco_count, b.views, b.category_name,bi.newfilename, bi.oriFileName ,b.nickname,b.reg_date FROM (SELECT ROW_NUMBER()OVER(ORDER BY b.bbs_idx DESC) AS rnum,b.bbs_idx, b.bbs_subject, b.bbs_content, b.reco_count, b.views, b.category_name ,b.nickname,reg_date FROM bbs b WHERE b.bbs_subject LIKE #{param2} OR b.bbs_content LIKE #{param2} ) b, bbs_img bi WHERE rnum BETWEEN #{param3} AND #{param4} AND b.bbs_idx=bi.bbs_idx(+)
AND CATEGORY_NAME= #{param1}
ORDER BY b.bbs_idx DESC
